/** * This is an Example service to load Employee data for the 'Employee' examples. * * Required Input parameters: * @param {Query} queryFilter the filter query object coming from the Grid-Advanced widget. Set default value in Mashup Builder * input parameters for the service to an empty object: {} * @return {InfoTable} with filtered and sorted data. * **/
var data = createData(); var params = { t: createInfoTable(data) /* INFOTABLE */, query: queryFilter /* QUERY INPUT PARAMETER*/ };
// result: INFOTABLE var result = Resources["InfoTableFunctions"].Query(params);
/** * Create an InfoTable for your data * @param {Array} data a two-dimensional array of data. * @returns {InfoTable} an InfoTable with data. */ function createInfoTable(data) { var params = { infoTableName: undefined /* STRING */ }; var result = Resources["InfoTableFunctions"].CreateInfoTable(params); //Add a new field to the InfoTable: result.AddField({name: "name", baseType: "STRING"}); result.AddField({name: "title", baseType: "STRING"}); result.AddField({name: "office", baseType: "STRING"}); result.AddField({name: "id", baseType: "NUMBER"}); result.AddField({name: "hiredate", baseType: "STRING"}); result.AddField({name: "salary", baseType: "NUMBER"});
var i = 0; for (i = 0; i < data.data.length; i++) { var employee = data.data[i]; result.AddRow({ 'name': employee[0], 'title': employee[1], 'office': employee[2], 'id': employee[3], 'hiredate': employee[4], 'salary': employee[5] }); } return result; }
