        'cold':			'#Cold Days: ' + days,
        'visibility':	'Total: {#stat_total}', // calculate total value on client side grid data.
        'wind': 		'Avg: {#stat_average}', // calculate average value on client side grid data.
        'events':		'',
        'image':		'',
        'location':		'',
        'actions':		'',
        'info':			''
    });
